.resident-ledger-filter-search .filters input[type=radio]{display:none;}.resident-ledger-filter-search .filters input[type=radio] + label{display:inline-block;margin:-2px;color:#333;background:#f7f7f7;border:1px solid #d1d1d1;font-size:12px;line-height:26px;min-height:24px;width:auto;text-align:center;padding:2px 8px;}.resident-ledger-filter-search .filters input[type=radio]:checked + label{background-image:none;background-color:#d0d0d0;box-shadow:inset 0 1px 1px #aaa;}.resident-ledger-filter-search .filters input[type=radio] + label.first{border-radius:3px 0 0 3px;}.resident-ledger-filter-search .filters input[type=radio] + label:last-child{border-radius:0 3px 3px 0;}.caret{margin-left:3px !important;}.add-action-btn{margin-bottom:5px !important;}.add-action-btn:first-child{margin-top:0px !important;}.add-action-btn:last-child{margin-bottom:0px !important;}.add-action-ul{margin-top:0px !important;padding:0px;}.add-action-ul > .add-action-btn:first-child > a{padding-top:5px;}.add-action-ul > .add-action-btn:last-child > a{padding-bottom:5px;}.modal-head .title span:first-child:empty{position:fixed;}